mapper.select:
  	select  from VOTE_HOUSE_INFO where info_id in (
  	select info_id from vote_scope_info where topic_id = #{topicId,jdbcType=DECIMAL} and 
  	info_id not in (select info_id from vote_result_info  where  
  	topic_id = #{topicId,jdbcType=DECIMAL}))

	       and  SECT_NAME=#{sectName,jdbcType=VARCHAR}


	       and  BUILD_CODE=#{buildCode,jdbcType=VARCHAR}


	      and  UNIT_CODE=#{unitCode,jdbcType=VARCHAR}


	      and  FLOOR_CODE=#{floorCode,jdbcType=VARCHAR}


	      and  ROOM_CODE=#{roomCode,jdbcType=VARCHAR}

  	select a.INFO_ID, a.INFO_ADDR, a.INFO_AREA, a.OWNER_NAME, a.LINK_PHONE, a.CERT_CODE, a.PROP_CODE, a.DIST_NAME, 
    a.SECT_ID, a.SECT_NAME, a.BUILD_CODE, a.UNIT_CODE, a.FLOOR_CODE, a.ROOM_CODE, a.HOUSE_ID, a.CREATE_DATE, 
    a.CREATE_TIME, a.CREATE_WAYS, a.INFO_STATUS, a.INFO_REMARK from VOTE_HOUSE_INFO a where 
    a.info_id not in (select info_id from vote_scope_info b where b.topic_id=#{topicId, jdbcType=DECIMAL} ) 

	       and a.SECT_NAME=#{sectName,jdbcType=VARCHAR}


	       and  a.BUILD_CODE=#{buildCode,jdbcType=VARCHAR}


	      and  a.UNIT_CODE=#{unitCode,jdbcType=VARCHAR}


	      and  a.FLOOR_CODE=#{floorCode,jdbcType=VARCHAR}


	      and   a.ROOM_CODE=#{roomCode,jdbcType=VARCHAR}

     order by a.info_id asc

  	select a.INFO_ID, a.INFO_ADDR, a.INFO_AREA, a.OWNER_NAME, a.LINK_PHONE, a.CERT_CODE, a.PROP_CODE, a.DIST_NAME, 
    a.SECT_ID, a.SECT_NAME, a.BUILD_CODE, a.UNIT_CODE, a.FLOOR_CODE, a.ROOM_CODE, a.HOUSE_ID, a.CREATE_DATE, 
    a.CREATE_TIME, a.CREATE_WAYS, a.INFO_STATUS, a.INFO_REMARK from VOTE_HOUSE_INFO a where 
    a.info_id in (select info_id from vote_scope_info b where b.topic_id=#{topicId, jdbcType=DECIMAL} ) 

	       and a.SECT_NAME=#{sectName,jdbcType=VARCHAR}


	       and  a.BUILD_CODE=#{buildCode,jdbcType=VARCHAR}


	      and  a.UNIT_CODE=#{unitCode,jdbcType=VARCHAR}


	      and  a.FLOOR_CODE=#{floorCode,jdbcType=VARCHAR}


	      and   a.ROOM_CODE=#{roomCode,jdbcType=VARCHAR}

     order by a.info_id asc
